-- Records of books
-- ----------------------------
INSERT INTO `books` VALUES (1001, 'Nguyễn Ngọc Thạch', '<p><span style=\"color:rgb(24, 24, 24); font-family:merriweather,georgia,serif; font-size:14px\">Năm năm sau Khóc giữa Sài Gòn, mỗi nhân vật chính lại có con đường đi riêng nhưng rồi không hẹn mà gặp, họ cùng nhau quay lại Sài Gòn, gặp những con người mới đến, còn bỡ ngỡ với vùng đất này và viết lên những câu chuyện mới.</span><br />\r\n<br />\r\n<span style=\"color:rgb(24, 24, 24); font-family:merriweather,georgia,serif; font-size:14px\">Đó là Khoa, cậu trai trẻ sớm mồ côi, bị ám ảnh bởi mùi biển và những xác người trắng phôi pha.</span><br />\r\n<br />\r\n<span style=\"color:rgb(24, 24, 24); font-family:merriweather,georgia,serif; font-size:14px\">Đó là Đăng, tham vọng nhưng dại khờ, ít tài năng nhưng chọn con đừơng bất chấp để thành công.</span><br />\r\n<br />\r\n<span style=\"color:rgb(24, 24, 24); font-family:merriweather,georgia,serif; font-size:14px\">Đó là Kate, người đàn bà bí ẩn với nụ cười nhếch môi, nhìn chuyện ồn ào ngoài kia như trò chơi của riêng mình.</span><br />\r\n<br />\r\n<span style=\"color:rgb(24, 24, 24); font-family:merriweather,georgia,serif; font-size:14px\">Đó là Khánh, người vợ cứ mãi băn khoăn trong câu hỏi liệu hạnh phúc gia đình phải giữ bằng cách nào.</span><br />\r\n<br />\r\n<span style=\"color:rgb(24, 24, 24); font-family:merriweather,georgia,serif; font-size:14px\">Và đó là Sinh vật Cô đơn, gã trai trẻ sống một cuộc đời xoay đều trong quỹ đạo của buồn tênh.</span><br />\r\n<br />\r\n<span style=\"color:rgb(24, 24, 24); font-family:merriweather,georgia,serif; font-size:14px\">Đó còn là Phan, mang ám ảnh về Nam. Là Mễ, vẫn đợi chờ người đàn ông của đời mình, là Ân, nay gai góc, xù xì như con nhím tự bảo vệ bản thân.</span><br />\r\n<br />\r\n<span style=\"color:rgb(24, 24, 24); font-family:merriweather,georgia,serif; font-size:14px\">Và họ cùng nhau lạc giữa những miền đau, lạc giữa mảnh đất mang tên Sài Gòn.</span><br />\r\n<br />\r\n<span style=\"color:rgb(24, 24, 24); font-family:merriweather,georgia,serif; font-size:14px\">Bạn, có sẵn sàng để cùng lạc với họ không?</span></p>\r\n', '2021-05-11 03:36:05', 'Người ta bảo yêu nhau 1 tháng với yêu nhau 1 năm khác nhau một trời một vực. Còn 3 năm thì không còn gì để nói. Công nhận là cũng hơi khác.', b'1', 'Zpl7_lac_giua_mien_dau.jpg', b'1', b'0', '2021-07-03 10:09:43', 'Lạc Giữa Niềm Đau', 152000, 5, 'lac-giua-niem-dau', 29);
INSERT INTO `books` VALUES (1002, 'Hamlet Trương', '<p><strong>Trích đoạn</strong></p>\r\n\r\n<p>“- Những ngày còn quen em, anh có lừa dối em lần nào không?</p>\r\n\r\n<p>– Không!</p>\r\n\r\n<p>– Làm sao để em tin điều đó?</p>\r\n\r\n<p>– Người đàn ông chân chính sẽ không bao giờ lừa dối người đàn bà mà anh ta kính trọng.”</p>\r\n\r\n<p>(Thiên Bình không ngủ)</p>\r\n\r\n<p>“Bạch Dương dự định hỏi xin thuốc giải cho chính cô, nhưng cô chợt ngừng lại vì… sĩ diện! Thật ra chuyện một tiên nữ mà bất cẩn đến nỗi trúng độc hoa Tình thì thực sự rất mất mặt, cô sẽ trở thành trò cười của mọi người mãi mãi về sau. Đối với Bạch Dương, sĩ diện là quan trọng nhất”</p>\r\n\r\n<p>(Bạch Dương trên đảo Đào Hoa)</p>\r\n\r\n<p>“Tôi cầm trên tay tấm bưu thiếp của Nhân Mã, tưởng tượng ra được nét mặt trẻ con và cái điệu bộ vừa đi từng nhịp nhanh vừa mở hết mắt, hết tai để nghe tiếng thiên nhiên gọi. Tôi thấy hạnh phúc mình trong hạnh phúc của anh, dù chúng tôi đã mấy tháng trời không thấy mặt”.</p>\r\n', '2021-05-11 03:37:19', 'Lần đầu tiên 12 chòm sao được cụ thể hóa thành những nhân vật có số phận, có tính cách đặc trưng trong những câu chuyện tình lãng mạn đến bất ngờ.', b'0', 'vtuu_12_cach_yeu.jpg', b'1', b'0', '2021-07-03 10:12:47', '12 Cách Yêu', 65000, 5, '12-cach-yeu', 28);
INSERT INTO `books` VALUES (1003, 'Mori Eto', '<p>OP 100 BEST SELLER – Có một người phạm tội nặng, chết đi không được luân hồi. Nhưng trong lúc linh hồn người này đang mất trí nhớ và trôi nổi vô định về một nơi tối tăm xứng đáng với cậu ta, thì một thiên sứ cánh trắng xuất hiện, giơ tay chặn lại, thông báo rằng cậu vừa trúng phiên xổ số may mắn của thiên đình, nhận được cơ hội tu hành kiêm tái thử thách. Theo đó, hồn cậu sẽ quay về trần gian, nhập vào xác một người vừa tự sát, ở trọ nhà người ta, nếm trải nhân tình thế thái và xử lý đống hỗn độn mà họ để lại. Mức độ hỗn độn tương đương với mức độ tội lỗi cậu phạm phải trong kiếp trước. Nếu việc tu hành diễn ra thuận lợi, ký ức kiếp trước sẽ dần dần quay về. Giây phút nhớ được tội lỗi mình đã gây ra cũng là lúc kết thúc quá trình ở trọ.</p>\r\n\r\n<p>Linh hồn cậu sẽ rời khỏi thân xác đi mượn và quay lên thiên giới, thuận lợi đặt chân vào vòng luân hồi, đầu thai sang kiếp khác. Colorful men theo những bất an bình thường mà ai cũng có thể gặp phải trong cuộc sống, nhưng một số người lại thấy chúng quá dị thường, quá kinh khủng, dẫn đến khổ tâm dằn vặt và rồi không thể vượt được qua. Để giải phóng những bình thường ấy, Colorful trình chiếu một giải pháp đặc biệt khác thường. Cuộc đời có muôn vàn nghiệm đúng, người ta tùy ý sống theo cách mình thích, nhưng một khi đã rút lui khỏi nó, thì chẳng còn nghiệm nào cho ta lựa chọn cả.</p>\r\n', '2021-05-11 03:38:08', 'Colorful (tạm dịch: Sắc màu) là quyển tiểu thuyết nổi tiếng của nữ tác giả Eto Mori, xuất bản năm 1998. Cuốn sách đã đạt giải thưởng Sankei Children’s Book Award lần thứ 46 – một trong những giải thưởng lớn và lâu đời nhất dành cho văn học thiếu nhi của Nhật Bản.', b'0', 'fAwP_colorfull.jpg', b'1', b'0', '2021-07-03 10:15:57', 'Colorful', 78000, 4, 'colorful', 28);
INSERT INTO `books` VALUES (1004, 'Đào', '<p style=\"text-align:justify\">Vẫn là câu chuyện về Tấm, Cám, dì ghẻ, đức vua với những tình tiết quen thuộc như thử giày, trèo cây hái cau, chim Vàng anh, cây xoan đào, quả thị nhưng lại có vô số uẩn khúc, bí mật được giữ kín đằng sau.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Tình yêu của đức vua chỉ có một, và nó dành cho cô gái có nhiều thứ hơn một đôi bàn chân đẹp.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Ở một nơi phép màu không tồn tại, để có được thứ mình muốn, đôi khi người ta cần đấu tranh và thậm chí trả giá thay vì ngồi một chỗ khóc và chờ Bụt xuất hiện.</p>\r\n\r\n<p style=\"text-align:justify\">Giá sản phẩm trên Tiki đã bao gồm thuế theo luật hiện hành. Tuy nhiên tuỳ vào từng loại sản phẩm hoặc phương thức, địa chỉ giao hàng mà có thể phát sinh thêm chi phí khác như phí vận chuyển, phụ phí hàng cồng kềnh, .....</p>\r\n', '2021-05-11 03:38:46', 'Sẽ thế nào nếu câu chuyện cổ tích kinh điển \"Tấm Cám\" được kể lại dưới một góc độ hoàn toàn khác, khi mà dì ghẻ thương con chồng như con đẻ, khi mà hai chị em cùng cha khác mẹ dành cho nhau tình cảm sâu đậm?', b'0', 'ASSW_co_tich_khong_phep_mau.jpg', b'1', b'0', '2021-07-03 10:17:53', 'Cổ Tích Không Phép Màu', 126000, 1, 'co-tich-khong-phep-mau', 28);
INSERT INTO `books` VALUES (1005, 'Koga Fumitake', '<p><em><strong>Dám Bị Ghét</strong></em></p>\r\n\r\n<p>Các mối quan hệ xã hội thật mệt mỏi.</p>\r\n\r\n<p>Cuộc sống sao mà nhạt nhẽo và vô nghĩa.</p>\r\n\r\n<p>Bản thân mình xấu xí và kém cỏi.</p>\r\n\r\n<p>Quá khứ đầy buồn đau còn tương lai thì mờ mịt.</p>\r\n\r\n<p>Yêu cầu của người khác thật khắc nghiệt và phi lý.</p>\r\n\r\n<p>TẠI SAO BẠN CỨ PHẢI SỐNG THEO KHUÔN MẪU NGƯỜI KHÁC ĐẶT RA?</p>\r\n\r\n<p>Dưới hình thức một cuộc đối thoại giữa Chàng thanh niên và Triết gia, cuốn sách trình bày một cách sinh động và hấp dẫn những nét chính trong tư tưởng của nhà tâm lý học Alfred Adler, người được mệnh danh là một trong “ba người khổng lồ của tâm lý học hiện đại”, sánh ngang với Freud và Jung. Khác với Freud cho rằng quá khứ và hoàn cảnh là động lực làm nên con người ta của hiện tại, Adler chủ trương “cuộc đời ta là do ta lựa chọn”, và tâm lý học Adler được gọi là “tâm lý học của lòng can đảm”.</p>\r\n\r\n<p>Bạn bất hạnh không phải do quá khứ và hoàn cảnh, càng không phải do thiếu năng lực. Bạn chỉ thiếu “can đảm” mà thôi. Nói một cách khác, bạn không đủ “can đảm để dám hạnh phúc”. […] Bởi can đảm để dám hạnh phúc bao gồm cả “can đảm để dám bị ghét” nữa. […] Chỉ khi dám bị người khác ghét bỏ, chúng ta mới có được tự do, có được hạnh phúc.</p>\r\n', '2021-05-11 03:39:43', 'Bạn bất hạnh không phải do quá khứ và hoàn cảnh, càng không phải do thiếu năng lực. Bạn chỉ thiếu “can đảm” mà thôi. Nói một cách khác, bạn không đủ “can đảm” để dám hạnh phúc.” […] Bởi can đảm để dám hạnh phúc bao gồm cả “can đảm để dám bị ghét” nữa. […] Chỉ khi dám bị người khác ghét bỏ, chúng ta mới có được tự do, có được hạnh phúc.', b'1', 'q0cT_dam_bi_ghet.jpg', b'1', b'0', '2021-07-03 10:20:08', 'Dám Bị Ghét', 65000, 7, 'dam-bi-ghet', 28);
INSERT INTO `books` VALUES (1006, 'Cao Minh', '<p>Hỡi những con người đang oằn mình trong cuộc sống, bạn biết gì về thế giới của mình? Là vô vàn thứ lý thuyết được các bậc vĩ nhân kiểm chứng, là luật lệ, là cả nghìn thứ sự thật bọc trong cái lốt hiển nhiên, hay những triết lý cứng nhắc của cuộc đời?</p>\r\n\r\n<p>Lại đây, vượt qua thứ nhận thức tẻ nhạt bị đóng kín bằng con mắt trần gian, khai mở toàn bộ suy nghĩ, để dòng máu trong bạn sục sôi trước những điều kỳ vĩ, phá vỡ mọi quy tắc. Thế giới sẽ gọi bạn là kẻ điên, nhưng vậy thì có sao? Ranh giới duy nhất giữa kẻ điên và thiên tài chẳng qua là một sợi chỉ mỏng manh: Thiên tài chứng minh được thế giới của mình, còn kẻ điên chưa kịp làm điều đó. Chọn trở thành một kẻ điên để vẫy vùng giữa nhân gian loạn thế hay khóa hết chúng lại, sống mãi một cuộc đời bình thường khiến bạn cảm thấy hạnh phúc hơn?</p>\r\n\r\n<p>Thiên tài bên trái, kẻ điên bên phải là cuốn sách dành cho những người điên rồ, những kẻ gây rối, những người chống đối, những mảnh ghép hình tròn trong những ô vuông không vừa vặn… những người nhìn mọi thứ khác biệt, không quan tâm đến quy tắc. Bạn có thể đồng ý, có thể phản đối, có thể vinh danh hay lăng mạ họ, nhưng điều duy nhất bạn không thể làm là phủ nhận sự tồn tại của họ. Đó là những người luôn tạo ra sự thay đổi trong khi hầu hết con người chỉ sống rập khuôn như một cái máy. Đa số đều nghĩ họ thật điên rồ nhưng nếu nhìn ở góc khác, ta lại thấy họ thiên tài. Bởi chỉ những người đủ điên nghĩ rằng họ có thể thay đổi thế giới mới là những người làm được điều đó.</p>\r\n', '2021-05-11 03:40:27', 'NẾU MỘT NGÀY ANH THẤY TÔI ĐIÊN, THỰC RA CHÍNH LÀ ANH ĐIÊN ĐẤY!', b'0', 'TxnB_thien_tai_ben_trai_ke_dien_ben_phai.jpg', b'1', b'0', '2021-07-03 10:21:08', 'Thiên Tài Bên Trái, Kẻ Điên Bên Phải', 124000, 7, 'thien-tai-ben-trai-ke-ien-ben-phai', 29);
INSERT INTO `books` VALUES (1007, 'Nguyễn Nhật Ánh', '<p>Mắt Biếc kể về cuộc đời của nhân vật chính tên Ngạn. Ngạn sinh ra và lớn lên ở một ngôi làng tên là Đo Đo. Lớn lên cùng với Ngạn là cô bạn hàng xóm có đôi mắt tuyệt đẹp tên là Hà Lan. Tuổi thơ của Ngạn và Hà Lan gắn bó với bao nhiêu kỉ niệm cùng đồi sim, đánh trống trường… Tình bạn trẻ thơ dần dần biến thành tình yêu thầm lặng của Ngạn dành cho Hà Lan. Đến khi lớn hơn một chút, cả hai phải rời làng ra thành phố để tiếp tục học. Khi tấm lòng của Ngạn luôn hướng về Hà Lan và về làng, thì Hà Lan không cưỡng lại được cám dỗ của cuộc sống xa hoa nơi đô thị và ngã vào vòng tay của Dũng.</p>\r\n\r\n<p>Việc Hà Lan ngã vào vòng tay Dũng – một thanh niên nhà giàu, sành điệu, giỏi võ nhưng rất thiếu đứng đắn – đã làm cho Ngạn đau khổ rất nhiều, nhưng điều Ngạn cần là hạnh phúc của Hà Lan. Mỗi khi Dũng làm Hà Lan tổn thương, cô lại tìm Ngạn để nói lên bầu tâm sự, điều đó lại càng làm cho Ngạn buồn thêm. Có lần Ngạn đã đánh nhau với Dũng vì Hà Lan và kết quả tất nhiên là Ngạn thua nhưng nó đã làm cho Ngạn bớt đi tính anh hùng, sẵn sàn đánh nhau mỗi khi Hà Lan bị bắt nạt. Và cũng từ đó những cảm xúc mỗi khi Hà Lan tìm đến Ngạn để giải bày niềm tâm sự giảm đi. Hà Lan mang thai, nhưng bị Dũng ruồng bỏ. Cô đành gửi con về cho bà ngoại chăm sóc và đặt tên là Trà Long. Tuy hiểu rõ tình yêu của Ngạn dành cho mình, Hà Lan vẫn không đáp lại vì cô hiểu rõ mình muốn sống theo một lối sống hoàn toàn khác với Ngạn.</p>\r\n\r\n<p>Bằng tình yêu của mình dành cho Hà Lan, Ngạn hết lòng yêu thương và chăm sóc cho Trà Long. Trà Long lớn lên trở thành cô giáo trường làng, và vô cùng yêu quí Ngạn. Trong khi ai cũng nghĩ rằng Trà Long sẽ là sự nối tiếp những gì mà Hà Lan đã bỏ dở trong đời Ngạn, thì Ngạn quyết định ra đi vì anh nghĩ rằng anh nhận ra rằng Trà Long chỉ là cái bóng của Hà Lan mà thôi.</p>\r\n\r\n<p>Một tác phẩm được nhiều người bình chọn là hay nhất của nhà văn này. Một tác phẩm đang được dịch và giới thiệu tại Nhật Bản (theo thông tin từ các báo)… Bởi sự trong sáng của một tình cảm, bởi cái kết thúc rất, rất buồn khi suốt câu chuyện vẫn là những điều vui, buồn lẫn lộn (cái kết thúc không như mong đợi của mọi người). Cũng bởi, mắt biếc… năm xưa nay đâu (theo lời một bài hát).</p>\r\n\r\n<p>Mắt biếc là một tác phẩm được nhiều người bình chọn là hay nhất của nhà văn Nguyễn Nhật Ánh. Tác phẩm này cũng đã được dịch giả Kato Sakae dịch sang tiếng Nhật để giới thiệu với độc giả Nhật Bản.</p>\r\n\r\n<p>“Tôi gửi tình yêu cho mùa hè, nhưng mùa hè không giữ nổi. Mùa hè chỉ biết ra hoa, phượng đỏ sân trường và tiếng ve nỉ non trong lá. Mùa hè ngây ngô, giống như tôi vậy. Nó chẳng làm được những điều tôi ký thác. Nó để Hà Lan đốt tôi, đốt rụi. Trái tim tôi cháy thành tro, rơi vãi trên đường về.”</p>\r\n\r\n<p>… Bởi sự trong sáng của một tình cảm, bởi cái kết thúc buồn, rất buồn khi xuyên suốt câu chuyện vẫn là những điều vui, buồn lẫn lộn…</p>\r\n', '2021-05-11 03:41:11', '“Mắt biếc’’ là câu chuyện về mối tình đơn phương dai dẳng giữa Ngạn và Hà Lan. Hai người bắt đầu bằng tình bạn ngây thơ trong sáng rồi dần dần tình cảm ấy lớn lên thành tình yêu nhưng tiếc là tình cảm ấy chỉ lớn lên trong Ngạn còn với Hà Lan, em đã sớm bỏ lại sau lưng làng Đo Đo- nơi cất giữ tuổi thơ của hai đứa, bỏ lại sau lưng những mùa hè râm ran tiếng ve, rực rỡ phượng thắm, bỏ lại Ngạn một mình cùng mối tình riêng, cùng những câu hát vu vơ không người nhắn gửi', b'0', 'ZaFf_mat_biec.jpg', b'1', b'0', '2021-07-03 10:21:52', 'Mắt Biếc', 156000, 4, 'mat-biec', 29);
INSERT INTO `books` VALUES (1008, 'Yuval Noah Harari', '<p>Những triển vọng đầy hứa hẹn của công nghệ sẽ được đưa ra bàn luận bên cạnh những hiểm họa như “đứt gãy” do công nghệ gây ra, việc kiểm soát thế giới bên trong dẫn tới sự sụp đổ của hệ thần kinh hay “tự do trong khuôn khổ”. Chính trị và tôn giáo có còn bắt tay nhau như trong quá khứ hay sẽ thao túng con người theo những cách riêng rẽ, mới mẻ hơn? Và những vấn đề toàn cầu ấy liên quan mật thiết tới hành vi và đạo đức của từng cá nhân riêng lẻ như thế nào? Xét cho cùng, những thách thức lớn nhất và những lựa chọn quan trọng nhất của ngày nay là gì? Ta cần chú ý đến điều gì? Ta nên dạy con cái ta những gì?</p>\r\n\r\n<p>“Cuộc khủng hoảng sinh thái đang lấp ló, mối đe dọa của các loại vũ khí hủy diệt hàng loạt […] và sự trỗi dậy của các công nghệ đột phá mới” là những nỗi lo không của riêng ai; và Harari sẽ đồng hành cùng bạn trên con đường bóc tách từng vấn đề một cách thấu đáo.</p>\r\n', '2021-05-11 03:41:36', 'Sau khi tìm hiểu quá khứ và tương lai của nhân loại qua hai cuốn sách gây tiếng vang là Sapiens và Homo deus, Yuval Noah Harari đi sâu vào các vấn đề “ngay tại đây” và “ngay lúc này”, tức các sự kiện hiện tại và tương lai gần nhất của xã hội loài người.', b'0', 'gDzr_21_bai_hoc_cho_the_ki_21.jpg', b'1', b'0', '2021-07-03 10:23:00', '21 Bài Học Cho Thế Kỉ 21', 134000, 3, '21-bai-hoc-cho-the-ki-21', 29);
INSERT INTO `books` VALUES (1009, 'Kulxsc', '<p>Từ cổ chí kim, con người ta sinh ra vốn đã một mình, có người bầu bạn thì tốt mà không có cũng chẳng sao.<br />\r\nAi cũng có thể tự kiếm tiền, tự lo cho bản thân, tự đứng dậy sau những vấp ngã. Buồn thì vui chơi với bạn bè, chán thì đi shopping, rảnh dỗi thì xem dăm ba bộ phim hài… biết bao thú vui có thể tự mình tận hưởng, không có người yêu thì đã làm sao?</p>\r\n\r\n<p>Nếu cả thế giới cho ta biết có người yêu hạnh phúc thế nào, ta cũng chẳng ngần ngại gì mà cho cả thế giới biết mình cô đơn vui vẻ ra làm sao.</p>\r\n\r\n<p>Cô đơn hay có người kề cạnh, cũng chỉ là một trạng thái. Ai cũng có lúc này, lúc kia. Và nếu như bạn đang cô đơn, thì đừng lo, bởi tình yêu luôn khó nói. Một ngày đẹp trời nọ, rất có thể nó sẽ ập vào đời bạn mà chẳng cần lý do.</p>\r\n\r\n<p>Tới lúc đó, bạn sẽ có cả hàng trăm việc để giải quyết, lo người yêu giận, nghĩ xem ngày kỷ niệm nên làm gì, đi hẹn hò thì ăn gì, mặc như thế nào… Chắc chắn lúc đó, bạn sẽ vô cùng bối rối và vô cùng lo lắng. Nhưng đó là chuyện của tương lai, còn bây giờ, bạn là người vô cùng tự do. Trong lúc đám bạn đang mải mê nghĩ cách dỗ dành người yêu thì bạn có thể đi bất cứ đâu, làm mọi việc mình thích và thả thính bất cứ ai mình muốn. Cô đơn cũng có nhiều điểm tốt đấy thôi !</p>\r\n\r\n<p>Vậy nên, thả lỏng và tận hưởng sự cô đơn đi nào. Vì chẳng ai thoát được tình yêu. Cho nên thi thoảng, cô đơn cũng là một phước lành ! Phải lòng với cô đơn chính là một cuốn sách mà ai cũng cần phải có dưới góc nhìn đầy mới mẻ và tươi trẻ của Kulzsc về sự cô đơn.</p>\r\n\r\n<p>Xóa tan cái định nghĩa cô đơn là nỗi buồn, Phải lòng với cô đơn là món quà dành tặng cho bất cứ ai vẫn đang một mình. Khi cô đơn, bạn hoàn toàn có thể tập trung để yêu thương chính mình, điều đó không phải tuyệt vời hay sao?</p>\r\n\r\n<p>Phải lòng với cô đơn mất rồi, nên cô đơn à, lại đây chơi! Chỉ là cô đơn thôi có gì đâu mà phải buồn, nhỉ. Vui lên nào vì bên cạnh chúng ta còn có sự cô đơn!</p>\r\n', '2021-05-11 03:42:17', 'Cô đơn ơi, lại đây chơi !', b'0', 'QoiC_phai_long_voi_co_don.jpg', b'1', b'0', '2021-07-03 10:23:45', 'Phải Lòng Với Cô Đơn', 99000, 1, 'phai-long-voi-co-on', 29);
INSERT INTO `books` VALUES (1010, 'Nguyễn Nhật Ánh', '<p>Viết về điều tốt đã không dễ, viết sao cho người đọc có thể đón nhận đầy cảm xúc tích cực, và muốn được hưởng, được làm những điều tốt dù nhỏ bé mới thật là khó. Làm bạn với bầu trời của Nguyễn Nhật Ánh đã làm được điều này.</p>\r\n\r\n<p>Như nhà văn từng phát biểu “điểm mạnh của văn chương nằm ở khả năng thẩm thấu. Bằng hình thức đặc thù của mình, văn chương góp phần mài sắc các ý niệm đạo đức nơi người đọc một cách vô hình. Bồi đắp tâm hồn và nhân cách một cách âm thầm và bền bỉ, đó là chức năng gốc rễ của văn chương, đặc biệt là văn chương viết cho thanh thiếu niên.”</p>\r\n', '2021-05-11 03:43:31', 'Một câu chuyện giản dị, chứa đầy bất ngờ cho tới trang cuối cùng. Vẻ đẹp lộng lẫy, vì lòng vị tha và tình yêu thương, khiến mắt rưng rưng vì một nỗi mừng vui hân hoan. Cuốn sách như một đốm lửa thắp lên lòng khát khao sống tốt trên đời.', b'0', '9Ix5_lam_ban_voi_bau_troi.jpg', b'1', b'0', '2021-07-03 10:24:12', 'Làm Bạn Với Bầu Trời', 166000, 2, 'lam-ban-voi-bau-troi', 29);
INSERT INTO `books` VALUES (1011, 'Jonathan Stroud', '<p><span style=\"color:rgb(64, 64, 64); font-family:lato,sans-serif; font-size:16px\">Vốn thích thể loại fantasy, Biển vô cùng Hài lòng vì “Cầu thang gào thét” là một quyển truyện rất rất hấp dẫn. Nếu chuyển thể thành phim thì Biển sẽ không dám xem vì biết trước nó khá đáng sợ, nhưng đọc sách thì không thấy sợ, tối vẫn ngủ ngon bình thường. Cuốn sách 355 trang được chia thành năm phần, sau phần đầu thì Biển có chút thắc mắc: chẳng lẽ quyển sách to dày như thế mà chỉ nói về một vụ trừ ma duy nhất. Nhưng không, tuy có một cốt truyện nhất quán xuyên suốt quyển sách nhưng các tình tiết&nbsp;</span><a class=\"st_tag internal_tag\" href=\"https://sachnhanam.com/tag/phat-trien/\" rel=\"tag\" style=\"box-sizing: inherit; color: rgb(52, 152, 219); text-decoration-line: none; font-family: Lato, sans-serif; font-size: 16px;\" title=\"Posts tagged with phát triển\">phát triển</a><span style=\"color:rgb(64, 64, 64); font-family:lato,sans-serif; font-size:16px\">&nbsp;theo hướng hoàn toàn bất ngờ, bối cảnh hành động thay đổi liên tục, đoạn sau sẽ giải thích cho đoạn trước, và không thiếu những yếu tố đánh đố óc phán đoán của người đọc.&nbsp;</span><a class=\"st_tag internal_tag\" href=\"https://sachnhanam.com/tag/tac-gia/\" rel=\"tag\" style=\"box-sizing: inherit; color: rgb(52, 152, 219); text-decoration-line: none; font-family: Lato, sans-serif; font-size: 16px;\" title=\"Posts tagged with tác giả\">Tác giả</a><span style=\"color:rgb(64, 64, 64); font-family:lato,sans-serif; font-size:16px\">&nbsp;Jonathan Stroud có bút lực vững chắc và lôi cuốn, khiến độc giả không muốn rời mắt đến khi đọc xong truyện. Thời gian thưởng thức sách cũng rất dễ chịu vì tuy “Cầu thang gào thét” to dày nhưng chỉ có một cuốn, cốt truyện tinh gọn không bị kéo dài mệt mỏi, (Biển không rõ có những tập tiếp theo không, nếu có thì Nhã Nam có dịch tiếp không). Trong lúc đọc, Biển thỉnh thoảng nhớ đến các bộ fantasy của nữ tác giả Cassandra Clare. Tuy những bộ sách của Clare đều từ 3~7 tập nhưng đọc vẫn lôi cuốn, mức độ yêu thích của Biển đối với truyện của hai tác giả này là ngang nhau.</span><br />\r\n<br />\r\n<span style=\"color:rgb(64, 64, 64); font-family:lato,sans-serif; font-size:16px\">“Những tiếng vọng ở đây không mạnh như trên dải ruy băng hay trên con dao, nó làm tôi nghĩ rằng chủ nhân của nó chưa chết – hoặc đã không chết trong khi đang đeo nó, theo bất cứ cách&nbsp;</span><a class=\"st_tag internal_tag\" href=\"https://sachnhanam.com/tag/nao/\" rel=\"tag\" style=\"box-sizing: inherit; color: rgb(52, 152, 219); text-decoration-line: none; font-family: Lato, sans-serif; font-size: 16px;\" title=\"Posts tagged with Nào\">nào</a><span style=\"color:rgb(64, 64, 64); font-family:lato,sans-serif; font-size:16px\">. Nhưng dù sao thì đã có cái chết gắn liền với nó. Rất nhiều cái chết. Và…nó không hề dễ chịu. Tôi đã nghe thấy tiếng ai đó cao giọng và..và gào thét…và… Đó là một thứ đê hèn. Tôi chẳng thể cầm nó được lâu. Tôi không biết nó là gì hay cậu kiếm được nó từ đâu, nhưng chẳng ai nên chạm vào thứ này, không bao giờ”.</span><br />\r\n<br />\r\n<span style=\"color:rgb(64, 64, 64); font-family:lato,sans-serif; font-size:16px\">Ba nhân vật trong “Cầu thang gào thét” được phân chia đất diễn đều nhau, tuy độc giả vẫn nhận ra ai là nam chính nữ chính nhưng đọc vẫn có cảm giác cân bằng, không ai bị&nbsp;</span><a class=\"st_tag internal_tag\" href=\"https://sachnhanam.com/tag/lu/\" rel=\"tag\" style=\"box-sizing: inherit; color: rgb(52, 152, 219); text-decoration-line: none; font-family: Lato, sans-serif; font-size: 16px;\" title=\"Posts tagged with lu\">lu</a><span style=\"color:rgb(64, 64, 64); font-family:lato,sans-serif; font-size:16px\">&nbsp;mờ. Độ tuổi của họ là 16~17 nhưng hành động và lời nói đều khá chín chắn (giống nhân vật trong các quyển truyện của Jonathan Auxier), tính cách dũng cảm không ấu trĩ, tuy vậy cũng không hề thiếu những đoạn đối thoại hơi hơi ngớ ngẩn và hài hước kể cả trong giây phút nguy hiểm tính mạng. Không có câu chữ nào rõ ràng về tình cảm của nam chính nữ chính nhưng các hint trong truyện cũng đủ khiến độc giả mơ mộng và hy vọng blah blah blah.</span><br />\r\n<br />\r\n<span style=\"color:rgb(64, 64, 64); font-family:lato,sans-serif; font-size:16px\">Đối với sách Nhã Nam, khoảng 10 cuốn thì có một cuốn Biển không thích – thường là do cốt truyện không hợp gu, nên Biển luôn trung thành và tự tin hiến máu cho Nhã Nam. Biển bắt đầu đọc “Cầu thang gào thét” khi đang rơi vào quãng thời gian chán sách, vì câu chuyện quá hay nên nó đã giúp con tim Biển vui trở lại. Càng vui hơn nữa khi giá bìa 145k nhưng Biển mua với giá 34k. Bìa sách đẹp, trình bày chỉn chu, dịch thuật tốt, nội dung hấp dẫn, tuy vậy còn một yếu tố về nam chính khiến Biển cho rằng có thể tác giả sẽ viết những tập tiếp theo. Cứ thường xuyên đọc được những quyển như thế này thì bảo đảm là Biển sẽ chẳng bao giờ thoát kiếp nghiện sách.</span></p>\r\n', '2021-05-11 03:44:07', 'Bối cảnh truyện có lẽ diễn ra vào đầu thế kỷ 20, khi tình trạng ma ám – tên gọi riêng là Vấn Đề – đang tác động lên toàn nước Anh. Các công ty thám tử săn lùng + tiêu diệt ma được thành lập và hoạt động năng nổ, với lượng nhân viên chính thức là trẻ con, vì trẻ con có năng lượng tinh sạch và khả năng phát hiện ma hiệu quả hơn người lớn. Khả năng này sẽ dần mất đi sau 20 tuổi. Công ty thám tử Lockwood & Đồng sự ở số 35 Portland Row gồm Anthony J. Lockwood, Lucy Joan Carlyle và George Cubbins là một công ty nhỏ, nhưng cả ba thành viên đều trẻ trung hoạt bát và tích cực làm việc. Mọi chuyện bắt đầu vào một buổi chiều mùa thu khi họ được yêu cầu đến trừ ma ở ngôi nhà số 62 đường Sheen, nơi có ông chồng đã ngã gãy cổ từ cầu thang.', b'0', 'j06A_cau_thang_gao_thet.jpg', b'1', b'0', '2021-07-03 10:28:54', 'Cầu Thang Gào Thét', 210000, 4, 'cau-thang-gao-thet', 30);
INSERT INTO `books` VALUES (1012, 'J.D. Barker', '<p>Với 4MK, tác giả J.D. Barker được đánh giá là thế hệ tiếp theo của Stephen King và Thomas Harris.</p>\r\n\r\n<p>Tên sách có vẻ gì khó hiểu, mang tính học thuật và toán học quá… thực chất để ý hình con Khỉ trang bìa thì đây là từ viết tắt của ‘The-4th-Monkey-Killer’ (con khỉ thứ 4 làm điều xấu…chưa hiểu lắm đúng không???), nếu dân văn phòng thì không xa lạ gì 4 bức hình nhân “chú tiểu”, chú thứ nhất: bịt tai – không nghe điều xấu, chứ thứ hai: bịt mắt – không thấy điều xấu, chú thứ ba: bịt miệng – không nói điều xấu, chú thứ tư: khoanh tay sau mông – không làm điều xấu… nhưng nguồn gốc thật sự thì bắt nguồn từ văn hóa Nhật Bản, bốn chú tiểu hiện hành bạn thấy trên bàn làm việc là tượng trưng cho bốn chú khỉ… và 4MK tượng trưng cho con khỉ thứ 4, và tất nhiên con khỉ này được hiện thực hóa trong khuông khổ…đã-làm-điều-xấu.</p>\r\n\r\n<p>Mình rất thích lối kể chuyện mở ra bằng một kết thúc, và trong tác phẩm này cũng vậy… một tai nạn xe buýt tình cờ đã tông chết kẻ thủ ác và kéo lê xác hắn tận 5m – kẻ đã thực hiện thành công các cuộc tra tấn về mặt thể xác lẫn tinh thần của bất kỳ nạn nhân nào hắn “vô tình” bắt được, tất nhiên là tài xế xe buýt vô can! Bằng bút pháp tả thực… từng chân tơ, kẻ tóc của nạn nhân được tác giả miêu tả đầy mang rợ, tạo cho người đọc cảm giác đang chứng kiến cái ác được diễn ra nhưng vô cùng câm lặng không thể thốt thành lời. Tên thủ ác lần lượt là cắt tai, cắt lưỡi, khoét mắt của nạn nhân và gửi từng bộ phận đấy về cho chính cha mẹ ruột của họ… trước khi phần còn lại của cái xác nằm gói gọn đâu đó bên mặt hồ tĩnh lặng hay công viên thanh vắng…</p>\r\n\r\n<p>Nếu các bạn đã đọc “Sự im lặng của bầy cừu” thì sẽ thấy quen thuộc với kiểu ra tay tàn ác của kẻ sát nhân nhưng (LƯU Ý: spoil sự im lặng của bầy cừu) Jeam Gumb giết nạn nhân xong mới lột từng tấm da lưng, thì trong căn phòng tối này 4MK tra tấn thể xác họ… ngay khi họ còn sống!!! Tất nhiên không có bất kỳ một cuộc dạo chơi hoặc thuật dẫn dắt tâm lý nào từ kẻ thủ ác trong 4MK (đỡ phải chịu cảm giác nhức nhói về mặt tâm lý so với đọc Sự Im lặng của bầy cừu!)</p>\r\n\r\n<p>Vẫn phong cách đặt vấn đề quen thuộc của mình… để một cuộc giết người diễn ra cần ba yếu tố “nguyên nhân – động cơ – mục đích”, thế nguyên nhân gì đã khiến 4MK thực hiện cuộc thanh trừng như một kẻ mộ điệu thực thi công lý, và tội ác của hắn hiễn nhiên thách thức các mật vụ điều tra liên bang suốt nữa thập kỳ để rồi một chiếc xe buýt vô tình cán chết hắn, tất nhiên là hắn vừa mới thực hiện thành công một ca “tùng xẻo tai của một bé gái mười sáu tuổi” và cái chết của hắn đặt ra một dấu chấm hỏi rất lớn… cái ác vẫn tiếp diễn cho dù kẻ thủ ác thật sự đã chết??? Không có bất kỳ dòng hồi tưởng nào của kẻ sát nhân, xin nhắc lại đây là bút pháp tả thực, không ảo mộng về quá khứ, không huyễn hoặc tự đặt mình vào vai kẻ sát nhân và tưởng tượng!</p>\r\n\r\n<p>Tuy nhiên kẻ thủ ác vẫn để lại cuốn nhật ký, và nội dung quyển nhật ký tuyệt đối không liên quan gì về cô gái mười sáu tuổi… cuốn nhật ký như một thái độ tự mãng, một lời thách thức đối với cục điều tra liên bang! Dưới nhận định của một trong các điều tra viên thì “hắn như muốn bất tử, dùng cái chết để trở nên vĩnh hằng”.</p>\r\n\r\n<p>Điều mình thật sự thích trong tác phẩm này là quá khứ và hiện thực được kể lại song song nhưng ngược lối, có những lúc mình chỉ muốn đọc liên tục nội dung cuốn nhật ký của kẻ sát nhân, để hiểu được nguyên nhân – động cơ – mục đích của cuộc thanh trừng này, và tất nhiên, bài toán không hề đơn giản như vậy, một lần nữa chính các độc giả cũng như các bạn sẽ bị lừa như chính các điều tra viên liên bang mà thôi!</p>\r\n\r\n<p>Cậu truyện trong nhật ký là gì, cô gái mười sáu tuổi rồi sẽ ra sao??? Tất cả đang chờ các bạn trong cuốn 4MK, và mình chốt hạ câu cuối, hiện thực tàn ác… không có chỗ cho cung đàn ảo mộng đâu, nên đừng đọc vài trang sách đã tự mãng đoán ra được cái kết! Nếu dễ dàng như vậy thì 4MK đã không đạt danh hiệu “Cuốn sách giành giải Audie năm 2018 cho thể loại Tiểu thuyết Kinh dị, gay cấn”!</p>\r\n', '2021-05-11 03:44:34', '“Nạn nhân đầu tiên, Calli T Hai ngày sau khi bị bắt cóc, gia đình Tremell nhận được tải con gái mình trong hòm thư. Hai ngày sau đó, họ nhận được đôi mắt của cô. Hai ngày tiếp theo, chiếc lưỡi của cô được chuyển đến. Thi thể của cô gái được tìm thấy tại công viên B Bàn tay cô nắm chặt một mẩu giấy nhỏ ghi KHÔNG LÀM ĐIỀU ÁC”', b'0', 'wmSR_4mk.jpg', b'1', b'0', '2021-07-03 10:30:12', '4MK', 323000, 8, '4mk', 30);
INSERT INTO `books` VALUES (1013, 'Osuichi', '<p>Mở đầu cuốn sách là câu chuyện về một người mẹ có hai đứa con gái sinh đôi. Kì lạ thay, bà chỉ yêu duy nhất một đứa còn đứa còn lại thì hành hạ không thương tiếc. Truyện thứ hai “Seven Rooms” kể về hai chị em bị bắt cóc đến bảy căn phòng và một tên sát nhân sẽ lần lượt giết người trong bảy căn phòng đấy. Có câu chuyện “Thơ của ánh dương” lại viết về một người máy được chế tạo ra phục vụ việc chôn cất cho người đã tạo ra mình. “Ngôi nhà trắng trong rừng sâu lạnh” lại kể về một cậu bé bị ngược đãi phải sống trong chuồng ngựa. Không chịu được cảnh khó khăn, cậu trốn thoát và chạy vào rừng sâu với quyết tâm xây một căn nhà chỉ toàn bằng xác người. Hay “Lời nguyền của chúa tể” kể về một chàng trai có thể nguyền rủa bất cứ ai, và cậu quyết định hủy diệt tất cả mọi người trên thế giới. Lại có câu chuyện hài hước trên cái nền u ám đấy – “Trên máy bay sắp rơi” khi nhân vật chính mua thuốc tự tử trước lúc máy bay rơi để không phải chịu cái chết đau đớn.</p>\r\n\r\n<p>Zoo đúng với tên gọi của nó. 11 câu chuyện khác nhau, 11 tính chất khác nhau, 11 sự kinh dị khác nhau. Nó giống như một sở thú với tạp nham đủ mọi loại người, mọi hành vi. Nhưng Zoo không phải một sở thú dành cho trẻ con, mà là một&nbsp;<span style=\"color:rgb(0, 0, 255); font-family:inherit\"><a href=\"https://waka.vn/the-loai/kinh-di-gregW.html\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; text-decoration-line: none; overflow-wrap: break-word; border: 0px; font-family: inherit; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline; cursor: pointer; color: rgb(0, 0, 255); word-break: normal; transition: all 0.15s ease-out 0s;\"><strong><em>tiểu thuyết kinh dị</em></strong></a>&nbsp;</span>dành cho người lớn. Chúng ta đi vòng quanh câu chuyện, chứng kiến, lắng nghe những “con thú” được nhốt ở đây đang gào thét, đang giở trò độc ác như thế nào.</p>\r\n', '2021-05-11 03:45:24', 'Zoo là cuốn tiểu thuyết kinh dị được viết bởi thiên tài Otsuichi. Cuốn sách gồm 11 truyện ngắn kinh dị khác nhau nhưng điểm chung của tất cả đều là sự u ám, đen tối đến tột cùng.', b'1', '8aqM_zoo.jpg', b'1', b'0', '2021-07-03 10:31:13', 'Zoo', 86000, 6, 'zoo', 30);
INSERT INTO `books` VALUES (1014, 'Ransom Riggs', '<p>Mồng 3 tháng 9 năm 1940.</p>\r\n\r\n<p>Vội vã nói lời tạm biệt hòn đảo từng là chốn náu thân yên ấm, mười đứa trẻ đặc biệt chạy trốn khỏi đám quái vật đang săn lùng mình. Chỉ có một người có thể giúp họ, cô phụ trách Peregrine, nhưng bà lại đang bị mắc kẹt trong hình dạng một con chim. Jacob Portman và bạn bè của cậu bắt đầu cuộc hành trình đầy bất trắc khó lường, hy vọng có thể tìm được người giúp cô phụ trách yêu quý trở lại dạng người. Nhưng nơi mà họ tìm đến, London năm 1940, đã là một thành phố tan hoang dưới tầm bom đạn, và những nguy hiểm chết người ẩn giấu mọi nơi, chưa kể thứ rùng rợn nhất: những xác sống, hồn rỗng. Với từng ấy thử thách, liệu đám trẻ có cứu được cô Peregrine?</p>\r\n\r\n<p>Là tập thứ 2 trong series “Trại trẻ đặc biệt của cô Peregrine”, Thành phố hồn rỗng tiếp nối một câu chuyện khiến độc giả mê hoặc. Như một nhà ảo thuật dày dạn, Ransom Rigg kết hợp văn phong hồi hộp với những bức ảnh minh họa kỳ lạ để củng cố bầu không khí ám ảnh của câu chuyện. Đáng sợ đến mức kinh ngạc nhưng cũng vô cùng cảm động, điểm đáng chỉ trích duy nhất của sách chính là nó buộc những độc giả cứ phải nôn nao khi kết thúc trang cuối – họ không muốn kìm lòng chờ đợi cho đến tập tiếp theo.</p>\r\n', '2021-05-11 03:46:01', '“Đó không phải là một con chim thật, thưa ông soát vé. Chúng cháu không bao giờ nghĩ tới việc vi phạm quy định như thế. Đây là món đồ chơi ưa thích của chị gái cháu, ông thấy đấy, và chị ấy nghĩ ông muốn lấy mất nó.” Cô xoắn hai bàn tay cầu khẩn đầy khổ sở. “Ông sẽ không lấy đi món đồ chơi ưa thích của một đứa trẻ chứ, phải không ông?”', b'0', 'muyu_thanh_pho_hon_rong.jpg', b'1', b'0', '2021-07-03 10:34:58', 'Thành phố hồn rỗng', 79000, 10, 'thanh-pho-hon-rong', 30);
INSERT INTO `books` VALUES (1015, 'Lôi Mễ', '<p>Trong thành phố C liên tiếp xảy ra 4 vụ cưỡng hiếp giết người, nạn nhân đều là những cô gái trí thức từ 25 – 35 tuổi, đây rốt cuộc là giết người trả thù hay đơn giản là cưỡng dâm?</p>\r\n\r\n<p>Hàng loạt cái chết bí ẩn thảm khốc của những người sống trong trường Đại học J liên tiếp xảy ra. Ở hiện trường vụ án, hung thủ đều để lại gợi ý cho vụ án tiếp theo, nhằm gợi ý gì?</p>\r\n\r\n<p>Trong hàng loạt các vụ án ly kỳ khiến cảnh sát bàng hoàng bó tay, Phương Mộc trầm mặc kiệm lời đột nhiên bị cảnh sát lôi vào cuộc. Tên ác quỷ giấu mặt lần lượt giết hại những người bạn của cậu, vì sao? Khi câu trả lời được vén màn bí mật, thì đề thi tàn khốc đã bị tích 5 dấu X đẫm máu.</p>\r\n\r\n<p>Một cuộc đấu trí so tài khốc liệt đầy kịch tính nổ Ai sẽ là người thắng cuộc?</p>\r\n\r\n<p>Đây là tác phẩm trinh thám được nhiều người biết đến nhất của Lôi Mễ – sĩ quan cảnh sát cấp phòng (sở) giảng dạy bộ môn Hình pháp học tại một trường cảnh sát trực thuộc Bộ Công an Trung Quốc, đồng thời cũng là một tác giả truyện trinh thám nổi tiếng. Truyện của Lôi Mễ luôn có sức hấp dẫn đặc biệt đối với độc giả bởi ngòi bút sắt bén với những tình tiết ly kì, lôi cuốn đến trang cuối cùng.</p>\r\n', '2021-05-11 03:46:40', 'Một tên sát thủ có sở thích uống chất hỗn hợp máu nạn nhân với sữa tươi, hắn có căn bệnh gì đặc biệt hay là con quỷ hút máu bất tử nghìn năm trong truyền thuyết?\r\n\r\n', b'0', 'G653_de_thi_dam_mau.jpg', b'1', b'0', '2021-07-03 10:35:42', 'Đề Thi Đẫm Máu', 150000, 15, 'e-thi-am-mau', 30);
INSERT INTO `books` VALUES (1016, 'Phạm Thị Thanh Thảo', 'Bên cạnh đó, Cấu trúc trong mỗi chủ đề gồm có: - Lý thuyết cơ bản - Các ví dụ minh họa - Bài tập ôn luyện theo từng bài - Hai đề thi thử chọn lọc kèm đáp án chi tiết kèm theo nhiều tài liệu và đề thi khác được gửi trong quá trình ôn luyện. Đặc biệt, các ô STUDY TIP, CHÚ Ý, NHẬN XÉT là những vấn đề cần lưu ý mà bạn đọc nên ghi nhớ để áp dụng trong kì thi. Ngoài ra, chúng tôi cũng cung cấp một số bài nâng cao cho các học sinh khá giỏi ôn luyện. Chúng tôi tin tưởng rằng cuốn sách “Công Phá Lí Thuyết Sinh” sẽ là tài liệu thiết thực và bổ ích cho các em học sinh đang chuẩn bị cho kỳ thi THPT Quốc gia cũng như đang học môn Sinh', '2021-05-11 03:47:26', 'Toàn bộ những nội dung lí thuyết, câu hỏi cần thiết trong lớp 10, lớp 11 và lớp 12 có thể xuất hiện trong đề của BGD 2019, 2020 đều được chúng tôi cập nhật đầy đủ. Đem đến cho các em bứt tranh tổng thể về Lý Thuyết Sinh, tối ưu cho việc học của em nhất. Chinh phục toàn bộ kiến thức lớp 10 - 11 - 12 chỉ trong một cuốn sách “Kinh điển”\r\n\r\n', b'0', 'RGXd_cong_pha_ly_thuyet_sinh.jpg', b'1', b'0', '2021-05-11 03:47:26', 'Công Phá Lý Thuyết Sinh', 144000, 5, 'cong-pha-ly-thuyet-sinh', 31);
